import { Injectable,Injector } from '@angular/core';

import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
pdfMake.vfs = pdfFonts.pdfMake.vfs;

import { File } from '@ionic-native/File/ngx';
import { FileOpener } from '@ionic-native/file-opener/ngx';

import {MySQLService} from './my-sql.service';

import { Platform } from '@ionic/angular';

@Injectable({
  providedIn: 'root'
})
export class PDFMakerService {
  mySQLService;

  constructor(private file: File, private fileOpener: FileOpener, public injector: Injector, private plt: Platform ) {
    this.mySQLService = injector.get(MySQLService);
   }

  pdfObj = null;
  recargo;

  //LLamado desde alert, crea comprobante en base a variables en mysql service
  createPdf(IdAlumno: number, Fecha: Date, MesAbonado: string, Recargo: boolean, Monto: any, NombreApellido:string) {
    if (!Monto){ 
      Monto = "0";
    }
    var recargoString;
    if (Recargo == false){
      recargoString = "No";
    } else {
      recargoString = "Si";
    }

    // Formato del PDF
    var docDefinition = 
    {
      content: 
      [
       /*
        //Doble columna
        {
        alignment: 'justify',
        columns: [
            [ //Columna1
              {text: 'Lorem ipsum dolor1'},
              {text: 'Lorem ipsum dolor2'}
            ],

            [ //Columna2
              {text: 'Lorem ipsum dolor3'},
              {text: 'Lorem ipsum dolor4'}
            ]
        ]
        },
      */

        //Logo formato base64
        {
          width: 180,
          alignment: 'center',
          image: 
          'data:image/jpeg;base64,',
        },
        
        { text: '\nComprobante', style: 'header', alignment: 'center'  },

        {
          style: 'tableExample',
          table: 
          {
            widths: ['*'], //1 sola columna de tabla, tamaño adaptable
            body: 
            [
              [ //Columna 1
                {
                  alignment: 'justify',
                  columns: 
                  [
                      [
                        { text: Fecha, alignment: 'left', style: 'date'},       //1
                        { text: 'Nombre:', style: 'subheader', alignment: 'left' },      //2
                        { text: 'Mes Abonado:', style: 'subheader', alignment: 'left' }, //3
                        { text: 'Recargo:', style: 'subheader', alignment: 'left' },     //4
                      ],
          
                      [
                        { text: "    " , alignment: 'left' , style: 'date'},                      //1
                        { text: NombreApellido, alignment: 'left' , style: 'subheaderInput'},    //2
                        { text: MesAbonado, alignment: 'left', style: 'subheaderInput'},       //3
                        { text: recargoString, alignment: 'left', style: 'subheaderInput'},   //4
                      ]
                  ]
                },
              ], //Fin columna 1
              [ //Columna 2
                {
                  alignment: 'justify',
                  columns: 
                  [
                      [
                        { text: 'Monto:', style: 'monto', alignment: 'left' },          //5
                      ],
                      [
                        {text: "$"+ (Math.round(parseFloat(Monto) * 100) / 100).toFixed(2).toString(), alignment: 'left', style: 'monto'},  
                      ]
                  ]
                },
              ] //Fin Columna 2
            ] // Fin body
          },
        }, //Fin tabla

      ],
      styles: {
        tableExample: {
          margin: [0, 5, 0, 15]
        },
        header: {
          fontSize: 22,
          bold: true,
        },
        date: {
          fontSize: 18,
          bold: true,
        },
        subheader: {
          fontSize: 14,
          bold: true,
          margin: [0, 15, 0, 0]
        },
        monto: {
          fontSize: 16,
          bold: true,
          margin: [0, 15, 0, 0]
        },
        subheaderInput: {
          fontSize: 14,
          margin: [0, 15, 0, 0]
        },
        story: {
          italic: true,
          alignment: 'center',
          width: '50%',
        }
      }
    }
    this.pdfObj = pdfMake.createPdf(docDefinition); //Crea el PDF
    this.downloadPdf();
  }
 
  //Descarga el PDF
  downloadPdf() {
    if (this.plt.is('cordova')) { //Si es en celular
      this.pdfObj.getBuffer((buffer) => {
        var blob = new Blob([buffer], { type: 'application/pdf' });
 
        // Save the PDF to the data Directory of our App
        this.file.writeFile(this.file.dataDirectory, 'comprobante.pdf', blob, { replace: true }).then(fileEntry => {
          // Open the PDf with the correct OS tools
          this.fileOpener.open(this.file.dataDirectory + 'comprobante.pdf', 'application/pdf');
        })
      });
    } else { //Si es en browser
      this.pdfObj.download();
    }
  }

}
